css:
 .panel {
    margin-left: 0px;
    background-color: #fff;
    border: 1px solid #97999B 0.7;
    border-radius: 4px;
    -webkit-box-shadow: 0 1px 1px rgba(0, 0, 0, .05);
    box-shadow: 3px 3px 0px rgba(0,0,0,0.18);
    width: 100%;
    height: 200px;  
   -webkit-transition: transform 2s ease-in-out;
    -moz-transition: transform 2s ease-in-out;
    transition: transform 2s ease-in-out;
}


.eachCatelog{
 max-height:239.95px; 
  min-height: 170px;
      background-origin: border-box;
    background-repeat: no-repeat;
    background-size: 150px 150px;
    background-position: bottom;
  
}


.margin_multi_catalog {
 margin-top:10px;
  padding: 10px;
}

.cust_pad {
 padding: 8px !important; 
}
.panel-title {
  color: #53565a;
    padding: 15px;
   text-align: -webkit-center;
  text-align: center;
    display: block;
}


.description{
   color: #53565a;
    font-weight: 800;
    margin: 0px 5px;
    font-size: 13px;
    margin-top: 0px;
  padding: 10px;
  
}

.panel-body {
    padding: 0;
}


.multi_catalog_mar {
 margin-top: 10px; 
}
.inc-image {
      width: 144px;
    height: 97px;
    position: absolute;
    padding: 0;
    margin-bottom: 0px !important;
    bottom: 50px;
    right: 66px;
}

.textBlock{
border-left:1px solid #97999B 0.7;
}



//Hover 



.text {
  color: red;
  font-size: 20px;
  position: absolute;
  top: 50%;
  left: 50%;
  -webkit-transform: translate(-50%, -50%);
  -ms-transform: translate(-50%, -50%);
  transform: translate(-50%, -50%);
  text-align: center;
}
